import classes from "./HeaderCartButton.module.css";
import Alert from "../../Authenticate/Alert";
import { useContext } from "react";
import CartContext from "../../store/cart-context";

const OrderedItems = (props) => {
  const [orderedItems, setOrderedItems] = userState([]);
  const orderedItemsHandler = () => {
    userid = localStorage.getItem("userid");
    Axios.post("http://localhost:3000/orderedItems", { userid: userid }).then(
      (response) => {
        if (response.data.length===0) {
          setOrderedItems([]);
          props.show
        }
        else{
            setOrderedItems(response.data)
        }
      }
    );
  };
  return (

    <button className={classes.button} onClick={orderedItemsHandler}>
      OrderedItems
    </button>
  );
};

export default OrderedItems;
