import { useState } from 'react';

import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';
import OrderCart from './components/Cart/OrderCart';
function App() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const [OrderCartIsShown, setOrderCartIsShown] = useState(false);
  const [orderedItems, setOrderedItems] = useState([]);
  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };
  const showOrders=()=>{
    setOrderCartIsShown(true);
    Axios.post("http://localhost:3000/orderedItems", { userid: localStorage.getItem("userid") }).then(
        (response) => {
          if (response.data.length===0) {
            setOrderedItems([]);
          }
          else{
              setOrderedItems(response.data)
          }
          props.onShowOrderedCart(orderedItems);
        }
      );

  }

  return (
    <CartProvider>
      {cartIsShown && <Cart onClose={hideCartHandler} />}
      {OrderCartIsShown && <OrderCart items={orderedItems}/>}
      <Header onShowCart={showCartHandler} showCart={showOrders} />
      <main>
        <Meals />
      </main>
    </CartProvider>
  );
}

export default App;
