import classes from "./HeaderCartButton.module.css";
import Login from "../../Authenticate/Login";
import Modal from "../UI/Modal";
import Axios from "axios";
import { useState } from "react";
import Signup from "../../Authenticate/Signup";
const HeaderLogin = (props) => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const showLoginHandler = () => {
    setShowLogin(true);

  };
  const hideLoginHandler=()=>{
      setShowLogin(false);
  }
  const hideSignupHandler=()=>{
      setShowSignup(false);
  }
  const showSignupHandler=()=>{
      setShowSignup(true);
      setShowLogin(false);
  }
  const loginHandler = (LoginData) => {
    setShowLogin(false);

    Axios.post("http://localhost:3000/authenticateUser", { loginData }).then(
      (response) => {
        if (response.data.found) {
          localStorage.setItem("userid", response.data.userid);
          localStorage.setItem("username", response.data.username);
          props.onAuthenticate();
        } else {
          setshowalert(true);
          setalertMsg("wrong username or password");
        }
      }
    );
  };

  return (
    <div>
      {showLogin && <Login onLogin={loginHandler} onClose={hideLoginHandler} onsignup={showSignupHandler}/>}
      {showSignup && <Signup  onClose={hideSignupHandler} onSignup={showLoginHandler}/>}
      <button className={classes.button} onClick={showLoginHandler}>
        Login
      </button>
    </div>
  );
};

export default HeaderLogin;
