    }).then((response) => {
      setOrderedItems(response.data);
      setOrderCartIsShown(true);
    });

  };

  return (
    <CartProvider>
       {showAlert&&<Alert alertMessage={"Logged out Successfully"} onClose={onClose}></Alert>}
      {cartIsShown && <Cart onClose={hideCartHandler} />}
